    public function FechaAccessor()
    {
    	$timestamp	=	$this->_get('Fecha');
    	
    	if ($timestamp)
    	{
        	$date 	=	new Classes_DateHelper();
        	
        	return $date->toViewFormat($timestamp);
    	}
    	else
    		return '';
    }
    
    
